import java.util.*;
public class Solution {
    public int minCut(String s) {
        int N = s.length();
        int[] dp = new int[N + 1]; // min cuts for s[i..N]
        boolean[][] isP = new boolean[N][N];
        for (int i = 0; i <= N; i++) {
            dp[i] = N - i - 1; //the worst case is cutting by each char
        }
        for (int i = N - 1; i >= 0; i--) {
            for (int j = i; j < N; j++) {
                isP[i][j] = s.charAt(i) == s.charAt(j) && (j - i < 2 || isP[i + 1][j - 1]);
                if (isP[i][j]) {
                    dp[i] = Math.min(dp[i], dp[j + 1] + 1);
                }
            }
        }
        return dp[0];
    }

    public static void main(String[] args) {
        Solution s = new Solution();
        System.out.println(s.minCut("aab"));
        System.out.println(s.minCut(""));
        System.out.println(s.minCut(""));
    }
}